import React from 'react'

export default function Todo({todo, toggleTodoCompleted}) {

    function checkchange(){
        toggleTodoCompleted(todo.id)
    }

  return (
    <div>
        <label>
        <input type="checkbox" checked={todo.complete} onChange={checkchange}></input>
        {todo.name}

        </label>
    </div>
  )
}
