import React, { useState , useRef, useEffect} from "react";
import TodoList from "./components/TodoList";
import { v4 as uuidv4 } from 'uuid'

const local_storage_key = "todoskeys"

function App(){
  const [todos, setTodos] = useState([])
  const todoNameRef = useRef()

  useEffect(() => {
    const localtodos = JSON.parse(localStorage.getItem(local_storage_key))

    if (localtodos) setTodos(localtodos)
  }, [])


  useEffect(() => {
    localStorage.setItem(local_storage_key, JSON.stringify(todos))


  }, [todos])


  function handeAddTodo(e){

   const name = todoNameRef.current.value

    if (name === "") { 
      return
    }
    setTodos(previousTodos =>{
      return [...previousTodos, {id: uuidv4(), name: "Todo 1", complete: false}]
    })

  }

  return (
    <div>
      <TodoList todos={todos}/>
      <input ref={todoNameRef}type="text"></input>
      <button onClick={handeAddTodo}>Add to Do</button>
      <button>Clear Completed</button>
      <div>0 left to do</div>
    </div>
  )

}
export default App
